package dkprograms;

public class Non_Staticmulti {
	
	void add() {
		
		int a=12;
		int b=30;
		int c=a+b;
		System.out.println(c);
	}
void sub() {
		
		int a=12;
		int b=30;
		int c=a-b;
		System.out.println(c);
	}void mul() {
		
		int a=12;
		int b=30;
		int c=a*b;
		System.out.println(c);
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Non_Staticmulti n1=new Non_Staticmulti();
		n1.add();
		n1.sub();
		n1.mul();
		
			
		

	}

}
